import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from '@reach/router';

const One = props => {
    const [onePet, setOnePet] = useState(null);
    const {pet} = props;
    const [reset, setReset] = useState(true);
    const deletePet = _id => {
        axios.delete(`http://localhost:8000/api/pet/delete/${_id}`)
            .then(() => setReset(!reset))
            .catch(err => console.log(err))
    }

    useEffect(() => {
        axios.get(`http://localhost:8000/api/pet/${props._id}`)
            .then(res => setOnePet(res.data))
            .catch(err => console.log(err))
    }, [props])
    return(
        <div className="container">
            <Link to="/"><p>back to home</p></Link>
            <button onClick={() => deletePet(pet._id)}> Adopt {onePet.petName} </button>
            {
            onePet ?
            <div className="container">
            <h1>Details about: {onePet.petName} </h1>
            <h3>Pet Type: {onePet.type} </h3>
            <h3>Description: {onePet.description} </h3>
            <h3>Skills: {onePet.skillOne} 
            {onePet.skillTwo}
            {onePet.skillThree}
            </h3>
            </div>: ""
            }
        </div>
    )
}

export default One;
